mapper.update:
UPDATE post
SET deleted = 1
WHERE postId = #{postId, jdbcType=BIGINT}

mapper.select:
SELECT postName, detailLink, companyName, companyLink, city, industry, time
FROM post
WHERE deleted = 0 AND postType = #{postType}

    AND (
    postName LIKE CONCAT(CONCAT('%', #{keyWord}), '%')
    OR companyName LIKE CONCAT(CONCAT('%', #{keyWord}), '%')
    )


    AND industry = #{industry}


    AND skill = #{skill}


    AND city LIKE CONCAT(CONCAT('%', #{city}), '%')


    AND salary = #{salary}


    AND education = #{education}



        AND TO_DAYS(time) = TO_DAYS(NOW())


        AND DATE_SUB(CURDATE(), INTERVAL 3 DAY) <= DATE(time)


        AND DATE_SUB(CURDATE(), INTERVAL 7 DAY) <= DATE(time)


        AND DATE_SUB(CURDATE(), INTERVAL 14 DAY) <= DATE(time)


        AND DATE_SUB(CURDATE(), INTERVAL 30 DAY) <= DATE(time)


        AND DATE_SUB(CURDATE(), INTERVAL 60 DAY) <= DATE(time)



    AND nature = #{nature}


    AND scale = #{scale}
